package react.debuglogmanager;

public class Logger{
    public static void debugStatement(int msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(Integer msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(Float msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(Double msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(String msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(char msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(Boolean msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public static void debugStatement(long msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
}