package react.debuglogmanager;

public class Logger{
    public void debugStatement(int msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public void debugStatement(Integer msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public void debugStatement(Float msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public void debugStatement(Double msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public void debugStatement(String msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
    public void debugStatement(char msg){
        System.out.println("DebugSym -> " + String.valueOf(msg));
    }
}