
package com.reactlibrary;

import com.reactlibrary.R;
import com.reactlibrary.Tablet;
import com.facebook.react.bridge.ReadableArray;
import com.facebook.react.uimanager.SimpleViewManager;
import com.facebook.react.uimanager.ThemedReactContext;
import com.facebook.react.uimanager.annotations.ReactProp;
import android.graphics.Color;
import java.util.ArrayList;
import react.debuglogmanager.Logger;
import react.debugtimer.MicroTimer;

public class ReactViewManager extends SimpleViewManager<Tablet>{

  public ThemedReactContext ctx;
  int pause = 1000;
  private Tablet tablet;

  public ReactViewManager(){
    super();
  }

  public Tablet getTablet(){
    return this.tablet;
  }

  @Override
  public String getName(){
    return "NativeTablet";
  }
  @Override
  public Tablet createViewInstance(ThemedReactContext reactContext){
    this.ctx = reactContext;
    Tablet tablet = new Tablet(this.ctx);
    this.tablet = tablet;
    return tablet;
  }
  @ReactProp(name = "CircleColor")
  public void setCircleColor(Tablet tablet, String rgb){
    tablet.setCircleColor(rgb);
  }
  @ReactProp(name = "LineColor")
  public void setLineColor(Tablet tablet, String rgb){
    tablet.setLineColor(rgb);
  }
  @ReactProp(name = "BackgroundColor")
  public void setBGColor(Tablet tablet, String rgb){
    tablet.setBGColor(rgb);
  }
  @ReactProp(name = "CircleRadius")
  public void setCircleRadius(Tablet tablet, float radius){
    tablet.setCircleRadius(radius);
    tablet.updateLineWidth();
  }
}
