
package react.viewmanager;

import com.facebook.react.uimanager.SimpleViewManager;
import com.facebook.react.uimanager.ThemedReactContext;
import com.facebook.react.uimanager.annotations.ReactProp;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.graphics.Bitmap;
import android.graphics.Canvas;
import android.graphics.Paint;
import android.graphics.RectF;
import android.graphics.drawable.BitmapDrawable;
import android.graphics.Color;



public class ReactViewManager extends SimpleViewManager<ImageView>{
  public void debugStatement(String msg){
    System.out.println("DebugSym -> " + msg);
  }
  @Override
  public String getName(){
    return "NativeImage";
  }
  @Override
  public ImageView createViewInstance(ThemedReactContext reactContext){
    ImageView pic = new ImageView(reactContext);
    LinearLayout.LayoutParams layoutParams = new LinearLayout.LayoutParams(0,0);
    pic.setLayoutParams(layoutParams);
    return pic;
  }
  @ReactProp(name = "heigth")
  public void setHeight(ImageView view, Integer height){
    view.getLayoutParams().height = height;
  }
  @ReactProp(name = "width")
  public void setWidth(ImageView view, Integer width){
    view.getLayoutParams().width = width;
  }
  @ReactProp(name = "bgcolor")
  public void setBGColor(ImageView view, Integer color){
    view.setBackgroundColor(Color.rgb(100, 200, 300));
  }
}
