package com.reactlibrary;
import react.debuglogmanager.Logger;

public class SimpleFloatVector{
    private float[] arr;
    private int originalCapacity;
    private int capacity;
    private int size = 0;
  
    public SimpleFloatVector(int capacity){
      this.capacity = capacity;
      this.originalCapacity = capacity;
      this.arr = new float[this.capacity];
    }
  
    public void add(float val){
      if(size == capacity){
        grow();
      }
      this.arr[size] = val;
      this.size++;
    }
  
    public float[] getArr(){
      return this.arr;
    }
  
    public void clearArr(){
      this.arr = new float[this.originalCapacity];
    }
  
    private void grow(){
      this.capacity *= 2;
      float[] temp = new float[this.capacity];
      for (int i = 0; i <= this.size - 1; i++){
        temp[i] = this.arr[i];
      }
    }
  
    public void printVec(){
      Logger.debugStatement("Pts: ");
      for (float i: this.arr){
        if(i == 0.0){
          continue;
        }
        Logger.debugStatement(i);
      }
    }
  }